import React from 'react'
import './formulario.css'

function Formulario() {
    function validar() {
        let email = document.getElementById('email')
        let telefone = document.getElementById('telefone')
        let form = document.getElementById('form-dot')
        let nome = document.getElementById('nome')

        if (nome.value === '') {
            alert('Nome incorreto,digite novamente!')
            nome.focus()
            return;
        }

        if (email.value.length > 0) {
            if (!IsEmail(email.value)) {
                alert('Email incorreto,digite novamente!')
                email.focus()
                return;
            }
        }
        if (telefone.value.length < 14) {
            alert('Telefone incorreto,digite novamente!')
            telefone.focus()
            return;
        }
        form.submit()
    }

    function IsEmail(email) {
        var exclude = /^[a-z0-9.]+@[a-z0-9]+\.[a-z]+(\.[a-z]+)?$/i
            ;
        if (email.search(exclude) != -1) { return true; }
        else { return false; }
    }


    return (
        <div>
            <form action="#" method="POST" id="form-dot" >
                <div class="container-background">
                    <div class="container-formulario">
                        <div class="formulario">
                            <div class="input-nome">
                                <input id="nome" type="text" required />
                                <div class="text-over-input">
                                    <span class="title">*Nome:</span><span class="subtitle">informe seu nome</span>
                                </div>
                            </div>
                            <div class="container-email-telefone">
                                <div class="email">
                                    <input id="email" class="input-email" type="text" required />
                                    <div class="text-over-input">
                                        <span class="title">*E-mail:</span><span class="subtitle">informe seu e-mail</span>
                                    </div>
                                </div>
                                <div class="telefone">
                                    <input id="telefone" onkeypress="setarMascara(this, mascaraTelefone);" onblur="setarMascara(this, mascaraTelefone);" class="input-telefone" type="text" required />
                                    <div class="text-over-input">
                                        <span class="title">*Telefone:</span><span class="subtitle">(__) ____-____</span>
                                    </div>
                                </div>
                            </div>
                            <div class="container-mensagem">
                                <textarea class="textarea-mensagem" required></textarea>
                                <div class="text-over-input">
                                    <span class="title">*Mensagem:</span><span class="subtitle">Escreva aqui</span>
                                </div>
                            </div>
                            <button class="btn-form" type="submit" onclick="validar()">Enviar</button>
                        </div>
                    </div>
                </div>

            </form>
            <div class="footer">
                LOREM IPSUM
        </div>
        </div>
    )
}

export default Formulario
